import React, { useState } from "react";
import ProductImages from "./ProductImages";
import "./ProductPage.css";
function ProductPage() {
  const st = 4.7;

  const [stars, setStars] = useState([]);

  let newS = [];

  const halfs = st - Math.floor(st);

  for (var i = 0; i < Math.floor(st); i++) newS.push(1);
  console.log(newS);
  if (halfs) newS.push(1 / 2);
  setStars(newS);

  return (
    <div style={{ marginTop: "60px" }}>
      <div className="card">
        <div className="path">
          HOME / FACE <a>/ CLEANSERS</a>{" "}
        </div>
        <div className="row">
          {/* images */}
          <div className="col-md-6 text-center align-self-center">
            {/* product images */}{" "}
            <img className="img-fluid" src="https://i.imgur.com/7a9V4yw.png" />{" "}
          </div>
          {/* Info */}
          <div className="col-md-6 info">
            <div className="row title">
              <div className="col">
                <h2>Product Title</h2>
              </div>
            </div>
            <p>product desc.</p>
            {stars.map((el) => {
              if (el == 1) {
                return <span className="fa fa-star checked"></span>;
              } else {
                return <span className="fa fa-star-half-full"></span>;
              }
            })}
            <span id="reviews">1590 Reviews</span>
            <div className="row price">
              <label className="radio">
                {" "}
                <input type="radio" name="size1" value="small" checked />
                <span>
                  <div className="row">
                    <big>
                      <b>1.5 oz.</b>
                    </big>
                  </div>
                  <div className="row">$12.95</div>
                </span>
              </label>
              <label className="radio">
                {" "}
                <input type="radio" name="size1" value="large" />{" "}
                <span>
                  <div className="row">
                    <big>
                      <b>4.4 oz.</b>
                    </big>
                  </div>
                  <div className="row">$21.95</div>
                </span>{" "}
              </label>{" "}
            </div>
          </div>
        </div>
        <div className="row lower">
          <div className="col">
            {" "}
            <a className="carousel-control-prev" href="#demo" data-slide="prev">
              <i className="fa fa-arrow-left"></i>
            </a>{" "}
            <a className="carousel-control-next" href="#demo" data-slide="next">
              <i className="fa fa-arrow-right"></i>
            </a>{" "}
          </div>
          <div className="col text-right align-self-center">
            {" "}
            <button className="btn">Add to cart</button>{" "}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductPage;
